import 'dart:io';import 'package:firebase_auth/firebase_auth.dart';import 'package:firebase_storage/firebase_storage.dart';import 'package:flutter/material.dart';import 'package:image_picker/image_picker.dart';//take imageFuture<File> takenImage(ImageSource source) async {  final picker = ImagePicker();  final pickedFile = await picker.pickImage(source: source);  return File(pickedFile!.path);}Future<String> getImagePath({required File path})async{  final _auth = FirebaseAuth.instance;  final FirebaseStorage _storage = FirebaseStorage.instance;  // Upload the new profile image to Firebase Storage  String imagePath = 'driver_driving_license/${_auth?.currentUser!.uid}.jpg';  UploadTask uploadTask = _storage.ref().child(imagePath).putFile(path!);  await uploadTask.whenComplete(() => null);  // Get the download URL of the uploaded image  String downloadURL = await _storage.ref(imagePath).getDownloadURL();  return downloadURL;}