import 'package:cloud_firestore/cloud_firestore.dart';import 'package:driver/view/bottom_navigation/screen/bootom_navigation.dart';import 'package:driver/widgets/app_toast.dart';import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/material.dart';class DocumentsController{  static final FirebaseAuth _auth = FirebaseAuth.instance;  static final FirebaseFirestore _firestore = FirebaseFirestore.instance;  static Future<bool> uploadDocuments({required String path, required BuildContext context})async{    try{      await _firestore.collection('drivers_profile').doc(_auth!.currentUser!.email).update({        "account_verify" : "1",        "driver_licence" : path      });      AppToast(text: "Documents Uploaded Success", color: Colors.green);      Navigator.push(context, MaterialPageRoute(builder: (context)=>AppBottomNavigation(pageIndex: 2,)));      return true;    }catch(e){      AppToast(text: "Something went wrong with server", color: Colors.red);      return false;    }  }}