import 'package:cloud_firestore/cloud_firestore.dart';import 'package:driver/view/bottom_navigation/screen/bootom_navigation.dart';import 'package:driver/widgets/app_toast.dart';import 'package:email_otp/email_otp.dart';import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/material.dart';class EmailVerificationController{  static final FirebaseAuth _auth = FirebaseAuth.instance;  static final FirebaseFirestore _firestore = FirebaseFirestore.instance;  static final  EmailOTP myauth = EmailOTP();  /// email verifiy by otp  static Future<bool> sendOTPIntoEmail({required String email}) async {    myauth.setConfig(        appEmail: "me@rohitchouhan.com",        appName: "AllOneAutos Driver",        userEmail: "nayon.coders@gmail.com",        otpLength: 4,        otpType: OTPType.digitsOnly    );    if (await myauth.sendOTP() == true) {      AppToast(text: "OTP send to your mail.", color: Colors.green);      return true;    } else {      print("adsfds === ${myauth}");      AppToast(text: "Oops, OTP send failed.", color: Colors.red);      return false;    }  }  /// otp verfiy  static Future<bool> verifyEmailOtp({required String OTP, required BuildContext context})async{    if (await myauth.verifyOTP(otp: OTP) == true) {      AppToast(text: "OTP is Verified.", color: Colors.green);      await _firestore.collection('drivers_profile').doc(_auth!.currentUser!.email).update({"email_verify" : "1"});      Navigator.push(context, MaterialPageRoute(builder: (context)=>AppBottomNavigation(pageIndex: 2,)));      return true;    } else {      AppToast(text: "Invalid OTP", color: Colors.green);      return false;    }  }}