import 'package:driver/utilitys/app_const.dart';import 'package:driver/utilitys/colors.dart';import 'package:driver/view/home/screens/home.dart';import 'package:flutter/material.dart';import 'package:font_awesome_flutter/font_awesome_flutter.dart';class AppBottomNavigation extends StatefulWidget {  const AppBottomNavigation({Key? key}) : super(key: key);  @override  State<AppBottomNavigation> createState() => _AppBottomNavigationState();}class _AppBottomNavigationState extends State<AppBottomNavigation> {  List<Widget> _page = [];  int _pageCount = 0;  @override  void initState() {    // TODO: implement initState    super.initState();    _page.insert(0, Home());    _page.insert(1, Home());    _page.insert(2, Home());    _page.insert(3, Home());  }  @override  Widget build(BuildContext context) {    var size = MediaQuery.of(context).size;    return Scaffold(      appBar: AppBar(        backgroundColor: AppColors.mainBg,        leading: Padding(          padding: const EdgeInsets.only(left: 20, top: 10, bottom: 10),          child: Image.asset(AppConst.app_logo, width: 100, height: 100,),        ),        elevation: 0,        leadingWidth: 100,        toolbarHeight: 60,        actions: [          Container(            width: 50,            height: 30,            margin: EdgeInsets.only(top: 5, bottom: 5),            decoration: BoxDecoration(              color: AppColors.white,              borderRadius: BorderRadius.circular(100),              border: Border.all(width: 1, color: AppColors.grey200)            ),            child: Center(              child: SizedBox(                height: 30,                width: 40,                child: Stack(                  children: [                    Icon(Icons.notifications, size: 40, color: AppColors.black,),                    Positioned(                      top: 0, right: 0,                        child: Container(                      width: 20,                      height: 20,                      decoration: BoxDecoration(                          color: AppColors.red,                          borderRadius: BorderRadius.circular(100),                      ),                          child: Center(                            child: Text("12",                              style: TextStyle(                                color: AppColors.white,                                fontSize: 12                              ),                            ),                          ),                    ))                  ],                ),              ),            ),          ),          SizedBox(width: 10,),          Container(            width: 50,            height: 30,            margin: EdgeInsets.only(top: 5, bottom: 5),            decoration: BoxDecoration(                color: AppColors.white,                borderRadius: BorderRadius.circular(100),                border: Border.all(width: 1, color: AppColors.grey200)            ),            child: Center(              child: Icon(Icons.person, size: 30, color: AppColors.black,),            ),          ),          SizedBox(width: 10,),        ],      ),      body: _page[_pageCount],      bottomNavigationBar: Container(        width: size.width,        height: 70,        decoration: BoxDecoration(          color: AppColors.white,          borderRadius: BorderRadius.only(            topLeft: Radius.circular(30),            topRight: Radius.circular(30),          ),          boxShadow: [            BoxShadow(              color: Colors.grey.shade200,              offset: Offset(0,1),              blurRadius: 10,              spreadRadius: 4            )          ]        ),        child:  BottomNavigationBar(        type: BottomNavigationBarType.fixed,        unselectedItemColor:Colors.grey,        selectedItemColor: AppColors.mainColor,        currentIndex: _pageCount,        items: [          BottomNavigationBarItem(            icon: FaIcon(Icons.dashboard, size: 30,),            label: 'Home',          ),          BottomNavigationBarItem(            icon: FaIcon(Icons.favorite, size: 30,),            label: 'Save',          ),          BottomNavigationBarItem(            icon: Icon(Icons.mark_unread_chat_alt),            label: 'Chat',          ),          BottomNavigationBarItem(            icon: Icon(Icons.settings),            label: 'Setting',          ),        ],          onTap: (index){            setState(() {              _pageCount = index;            });          },      ),      ),    );  }}