import 'package:driver/utilitys/app_const.dart';import 'package:driver/utilitys/colors.dart';import 'package:flutter/material.dart';import '../widgets/home_search.dart';class Home extends StatefulWidget {  const Home({Key? key}) : super(key: key);  @override  State<Home> createState() => _HomeState();}class _HomeState extends State<Home> {  final _searchController = TextEditingController();  @override  Widget build(BuildContext context) {    var size = MediaQuery.of(context).size;    return Container(      width: size.width,      height: size.height,      padding: EdgeInsets.all(20),      child: SingleChildScrollView(        child: Column(          mainAxisAlignment: MainAxisAlignment.start,          crossAxisAlignment: CrossAxisAlignment.start,          children: [            SizedBox(                width: size.width*.70,                child: Text("Let's find you facourite car here.",              style: TextStyle(                fontSize: 25,                fontWeight: FontWeight.w600,                color: AppColors.black              ),            )),            ///            ///======= search ========            SizedBox(height: 20,),            HomeSearch(              controller: _searchController,              onFilter: (){},              onChanged: (v){},            ),            ///            ///            ///=========  top brands ======            SizedBox(height: 20,),             Column(               children: [                 Row(                   mainAxisAlignment: MainAxisAlignment.spaceBetween,                   children:  [                      Text("Trading Brand's",                        style: TextStyle(                          fontSize: 20,                          fontWeight: FontWeight.w600,                          color: AppColors.black                        ),                      ),                     TextButton(onPressed: (){}, child: Text("VIEW ALL"))                   ],                 ),                 SizedBox(height: 10,),                 SizedBox(                   height: 110,                   child: ListView.builder(                     scrollDirection: Axis.horizontal,                     itemCount: 10,                     itemBuilder: (_, index){                       return Container(                           margin: EdgeInsets.only(right: 20),                           child: Column(                         children: [                           Container(                             width: 80,                             height: 80,                             padding: EdgeInsets.all(15),                             decoration: BoxDecoration(                               color: AppColors.white,                               borderRadius: BorderRadius.circular(5)                             ),                             child: Image.asset(AppConst.brand_image, fit: BoxFit.contain,),                           ),                           SizedBox(height: 10,),                           Text("BMW",                            style: TextStyle(                              fontSize: 13,                              color: AppColors.black,                              fontWeight: FontWeight.w600                            ),                           )                         ],                       ));                   },                   ),                 )               ],             )          ],        ),      ),    );  }}