import 'package:driver/utilitys/colors.dart';import 'package:flutter/material.dart';class NotificationView extends StatefulWidget {  const NotificationView({Key? key}) : super(key: key);  @override  State<NotificationView> createState() => _NotificationViewState();}class _NotificationViewState extends State<NotificationView> {  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: AppColors.mainBg,      appBar: AppBar(        backgroundColor: Color(0xffF3F3F3),        elevation: 0,        title: const Text("Notification",          style: TextStyle(              color: AppColors.black,              fontSize: 19          ),        ),        leading: InkWell(            onTap: ()=>Navigator.pop(context),            child: Container(              width: 40,              height: 40,              margin: EdgeInsets.all(10),              decoration: BoxDecoration(                color: Color(0xffD9D9D9),                borderRadius: BorderRadius.circular(5),              ),              child: Center(                child: Icon(Icons.arrow_back, color: AppColors.black, size: 20,),              ),            )),        actions: [          Container(            width: 40,            height: 40,            margin: EdgeInsets.all(10),            decoration: BoxDecoration(              color: Color(0xffD9D9D9),              borderRadius: BorderRadius.circular(5),            ),            child: Center(              child: Icon(Icons.settings, color: AppColors.black, size: 20,),            ),          ),        ],      ),      body: ListView.builder(        itemCount: 20,        itemBuilder: (_, index){          return Container(            padding: EdgeInsets.only(left: 20, right: 20),            decoration: BoxDecoration(              color:index.isEven ? Colors.white : Colors.grey.shade300            ),            child: ListTile(              title: Text("New Car Updated"),              subtitle: Text("12-12-2023"),            )          );        },      )    );  }}