import 'package:driver/widgets/appButton.dart';import 'package:flutter/material.dart';class AppAler{  static Future<void> showMyDialog({required String message, required BuildContext context}) async {    return showDialog<void>(      context: context,      barrierDismissible: false, // user must tap button!      builder: (BuildContext context) {        return AlertDialog(          content: SingleChildScrollView(            child: ListBody(              children: <Widget>[                Icon(Icons.check_circle, color: Colors.green, size: 80,),                SizedBox(height: 10,),                Text(message),                SizedBox(height: 20,),                AppButton(text: "Ok", onClick: ()=>Navigator.pop(context)),              ],            ),          ),        );      },    );  }  static  Future appAlert({required String title, required String message, required BuildContext context})async{    return showDialog(      context: context,      builder: (BuildContext context) {        return AlertDialog(          content: Container(              width: MediaQuery.of(context).size.width*0.8,              height: MediaQuery.of(context).size.height*0.3,              decoration: BoxDecoration(                borderRadius: BorderRadius.circular(10),                color: Colors.white,              ),              child: Column(                children: <Widget>[                  Icon(Icons.check_circle, color: Colors.green, size: 80,),                  SizedBox(height: 10,),                  Text(message),                  SizedBox(height: 20,),                  AppButton(text: "Ok", onClick: ()=>Navigator.pop(context)),                ],              )),        );      },    );  }}