import 'package:driver/utilitys/colors.dart';import 'package:flutter/material.dart';class AppInput extends StatelessWidget {  const AppInput({    super.key, required this.title, required this.hintText, required this.controller, this.obscureText = false,  this.suffixIcon, this.prefixIcon, this.validator,  });  final String title;  final String hintText;  final TextEditingController controller;  final bool obscureText;  final Widget? suffixIcon;  final Widget? prefixIcon;  final String? Function(String?)? validator;  @override  Widget build(BuildContext context) {    return Column(      mainAxisAlignment: MainAxisAlignment.start,      crossAxisAlignment: CrossAxisAlignment.start,      children: [        Text("$title:",          style: TextStyle(              fontWeight: FontWeight.w500,              color: AppColors.black,              fontSize: 17          ),        ),        SizedBox(height: 5,),        TextFormField(          controller: controller,          obscureText: obscureText,          decoration: InputDecoration(              fillColor: AppColors.white,              filled: true,              border: OutlineInputBorder(                  borderRadius: BorderRadius.circular(5),                  borderSide: BorderSide.none              ),              enabledBorder: OutlineInputBorder(                  borderRadius: BorderRadius.circular(5),                  borderSide: BorderSide.none              ),              focusedBorder: OutlineInputBorder(                  borderRadius: BorderRadius.circular(5),                  borderSide: BorderSide.none              ),              contentPadding: EdgeInsets.only(left: 15, right: 15),              hintText: "$hintText",              hintStyle: TextStyle(                  fontSize: 13,                  fontWeight: FontWeight.w300              ),            suffixIcon: suffixIcon,            prefixIcon: prefixIcon,          ),          validator: validator,        )      ],    );  }}