import 'package:cached_network_image/cached_network_image.dart';import 'package:flutter/material.dart';import 'app_shimmer.dart';class AppNetWorkImage extends StatelessWidget {  const AppNetWorkImage({Key? key, required this.url, required this.height, required this.width, this.boxFit = BoxFit.cover}) : super(key: key);  final double height;  final double width;  final BoxFit boxFit;  final String url;  @override  Widget build(BuildContext context) {    return CachedNetworkImage(      imageUrl: "$url",      height: height,      width: width,      fit: boxFit,      progressIndicatorBuilder: (context, url, downloadProgress) =>          AppShimmer(width: 130, height: 130),      errorWidget: (context, url, error) => Icon(Icons.error),    );  }}