import 'package:driver/utilitys/colors.dart';import 'package:flutter/material.dart';import 'package:shimmer/shimmer.dart';class AppShimmer extends StatelessWidget {  const AppShimmer({Key? key, required this.width, required this.height}) : super(key: key);  final double width;  final double height;  @override  Widget build(BuildContext context) {    return Container(      width: width,      height: height,      decoration: BoxDecoration(        borderRadius: BorderRadius.circular(10)      ),      child: Shimmer.fromColors(        baseColor: Colors.white,        highlightColor: AppColors.grey200,        child: Container(          color: Colors.white,        )      ),    );  }}