import 'package:driver/utilitys/colors.dart';import 'package:flutter/material.dart';import 'appButton.dart';import 'app_network_image.dart';class CarListView extends StatelessWidget {  const CarListView({    super.key,    required this.size, required this.name, required this.amount, required this.rentVeriant, required this.rentReviews, required this.totalReviews, required this.onAddFav, required this.image, this.isFav = false, required this.onClick,  });  final Size size;  final String image;  final String name;  final String amount;  final String rentVeriant;  final String rentReviews;  final String totalReviews;  final VoidCallback onAddFav;  final VoidCallback onClick;  final bool isFav;  @override  Widget build(BuildContext context) {    return InkWell(      onTap: onClick,      child: Container(        width: size.width,        padding: EdgeInsets.all(10),        margin: EdgeInsets.only(bottom: 20,left: 5, right: 5),        decoration: BoxDecoration(            color: AppColors.white,            borderRadius: BorderRadius.circular(10),            boxShadow: [              BoxShadow(                  color: Colors.grey.shade200,                  spreadRadius: 3, blurRadius: 5, offset: Offset(0,1)              )            ]        ),        child: Row(          children:  [            SizedBox(width: 130, child: AppNetWorkImage(url: "$image", height: 130, width: 130, boxFit: BoxFit.contain, )),            SizedBox(width: 10,),            Expanded(child: Column(              mainAxisAlignment: MainAxisAlignment.start,              crossAxisAlignment: CrossAxisAlignment.start,              children:  [                Text("$name",                  style: TextStyle(                  fontWeight: FontWeight.w600,                  fontSize: 16,                  color: AppColors.black                  ),                ),                SizedBox(height: 10,),                RichText(                    text: TextSpan(                        children: [                          TextSpan(                              text: "\$${amount}",                              style: TextStyle(                                  fontSize: 14,                                  fontWeight: FontWeight.w600,                                  color: AppColors.black                              )                          ),                          TextSpan(                              text: "/$rentVeriant",                              style: TextStyle(                                  fontSize: 14,                                  fontWeight: FontWeight.w400,                                  color: Colors.grey                              )                          )                        ]                    )                ),                SizedBox(height: 10,),                Row(                  children: [                    Icon(Icons.star, color: Colors.amber, size: 20,),                    SizedBox(width: 3,),                    Text("$rentReviews",                        style: TextStyle(                            fontSize: 14,                            fontWeight: FontWeight.w400,                            color: Colors.grey                        )                    ),                    SizedBox(width: 3,),                    Text("($totalReviews)",                        style: TextStyle(                            fontSize: 14,                            fontWeight: FontWeight.w400,                            color: Colors.grey                        )                    )                  ],                ),                SizedBox(height: 10,),                SizedBox(                    width: size.width*.45,                    child: Row(                  mainAxisAlignment: MainAxisAlignment.spaceBetween,                  children: [                    AppButton(                        width: 100,                        height: 35,                        fontSize: 13,                        text: "Rent Now",                        onClick: (){}                    ),                    InkWell(                      onTap: (){},                      child: Icon(Icons.favorite, color: AppColors.red,),                    )                  ],                ))              ],            ))          ],        ),      ),    );  }}