import 'dart:async';import 'package:driver/utilitys/colors.dart';import 'package:flutter/material.dart';class ResendOtp extends StatefulWidget {  final VoidCallback onClick;  const ResendOtp({Key? key, required this.onClick}) : super(key: key);  @override  State<ResendOtp> createState() => _ResendOtpState();}class _ResendOtpState extends State<ResendOtp> {  int _secondsRemaining = 120; // 2 minutes = 120 seconds  late Timer _timer;  @override  void initState() {    // TODO: implement initState    super.initState();    _startTimer();  }  @override  void dispose() {    _timer.cancel();    super.dispose();  }  void _startTimer() {    const oneSecond = Duration(seconds: 1);    _timer = Timer.periodic(oneSecond, (timer) {      if (_secondsRemaining == 0) {        _timer.cancel();        // Timer completed, perform any desired actions here        print('Timer completed');      } else {        setState(() {          _secondsRemaining--;        });      }    });  }  String _formatTime(int seconds) {    int minutes = seconds ~/ 60;    int remainingSeconds = seconds % 60;    return '$minutes:${remainingSeconds.toString().padLeft(2, '0')}';  }  @override  Widget build(BuildContext context) {    return  InkWell(        onTap:  _secondsRemaining != 00 ? null : widget.onClick,        child: RichText(text: TextSpan(            children: [              TextSpan(                  text: "I don't get the code. ",                  style: TextStyle(                      fontWeight: FontWeight.w400,                      fontSize: 15,                      color: AppColors.black                  )              ),              TextSpan(                  text: "${_secondsRemaining != 00 ? "wait ${ _formatTime(_secondsRemaining)}" : "Send again"}",                  style: TextStyle(                      fontWeight: FontWeight.w600,                      fontSize: 16,                      color: AppColors.mainColor                  )              )            ]        )));  }}